#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    int n;    cin>>n;    vector<int> a(n);    for (int i=0;i<n;i++){        cin>>a[i];    }    sort(a.begin(),a.end(),greater<int>());    for(int i=0;i<n;i++){        cout<<a[i]<<" ";    }    cout<<endl; } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}