#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll n;    cin >> n;    string s;    cin >> s;     ll balance = 0;    ll c=0;     for (char ch : s) {        if (ch == '(') balance++;        else balance--;        if(balance<0){            c++;            balance=0;        }    }      cout << c << "\n";} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     ll t;    cin >> t;    while (t--) solve();}