#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;    cin>>n;    while(1){        ll c=0;        string s=to_string(n);        for(auto i:s){            if(i!='0' && n%(i-'0')!=0){                n++;                break;            }            c++;        }         if(c==s.size()){            cout<<n<<endl;            break;        }            }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}