#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    int n,m;    cin>>n>>m;    vector<vector<int>> arr(n,vector<int>(m));    ll sum=0;    int mn=INT_MAX;    int c=0;    int z=0;    for(int i=0;i<n;i++){        for(int j=0;j<m;j++){            cin>>arr[i][j];            sum+=abs(arr[i][j]);            mn=min(mn,abs(arr[i][j]));            if(arr[i][j]<0){                c++;            }            if(arr[i][j]==0){                z++;            }        }    }    if(c%2==0){        cout<<sum<<endl;        return;    }    else{        if(z>=1){            cout<<sum<<endl;        }        else{            cout<<sum-2*mn<<endl;        }    }  } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}