#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>;bool prime_check(ll n){        for(ll i=2;i<n;i++){        if(n%i==0){            return false;        }    }    return true;} void solve() {    ll d;    cin>>d;    ll a=1+d;    while(!prime_check(a)){        a++;    }    ll b=a+d;    while(!prime_check(b)){        b++;    }    ll ans=a*b;    cout<<ans<<endl; } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}