#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll a,b;    cin>>a>>b;        ll mn=1e9;            for(ll i=0;i*i<=a;i++){   //first of all add 1 to b i no. times         ll cn=i;   //        if(b==1 && i==0){            continue;        }        ll an=a;        while(an>0){   //then divide until an becomes 0            an=an/(b+i);            cn++;        }        mn=min(mn,cn);    }    cout<<mn<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}