#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    string s1, s2;    cin >> s1 >> s2;    int n1 = s1.size();    int n2 = s2.size();     int mx = 0;     for (int i = 0; i < n1; i++) {        for (int j = 0; j < n2; j++) {            int k = 0;            while (i + k < n1 && j + k < n2 && s1[i + k] == s2[j + k]) {                k++;                mx = max(mx, k);            }        }    }     cout << (n1 + n2 - 2 * mx) << "\n";} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}