#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll a,b;    cin>>a>>b;    ll xr=0;    if(a%4==0){        xr=0;    }    else if(a%4==3){        xr=a;    }    else if(a%4==2){        xr=1;    }    else if(a%4==1){        xr=a-1;    }    xr=xr^b;    if(xr==0){        cout<<a<<endl;    }    else if(xr==a){        cout<<a+2<<endl;    }    else {        cout<<a+1<<endl;    }         } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}