#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll n;    cin >> n;    vector<pair<ll,ll>> arr(n);    for (ll i = 0; i < n; i++) {        ll ele;        cin >> ele;        arr[i]={ele,i+1};    }     sort(arr.begin(),arr.end());    vector<ll> ans(n+1);    ll cost=0;    ll p=1;    for(ll i=n-1;i>=0;i-=2){        cost+=arr[i].first*p;        ans[arr[i].second]=p;        if(i-1>=0){            cost+=arr[i-1].first*p;            ans[arr[i-1].second]=p*-1;        }        p++;    }    cout<<2*cost<<endl;             for(auto ele:ans){        cout<<ele<<" ";    }    cout<<endl;        } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}