#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll w, h;    cin >> w >> h;    vector<vector<ll>> sides;     int x = 4;    while (x--) {        ll n;        cin >> n;        vector<ll> aa(n);        for (int i = 0; i < n; i++) {            cin >> aa[i];        }        sides.push_back(aa);    }    ll mx=0;    //y=0    ll x1=sides[0][0];    ll x2=sides[0][sides[0].size()-1];    ll base=x2-x1;    mx=max(base*h,mx);     //y=h    x1=sides[1][0];    x2=sides[1][sides[1].size()-1];    base=x2-x1;    mx=max(base*h,mx);     //x=0    x1=sides[2][0];    x2=sides[2][sides[2].size()-1];    base=x2-x1;    mx=max(base*w,mx);     //x=w    x1=sides[3][0];    x2=sides[3][sides[3].size()-1];    base=x2-x1;    mx=max(base*w,mx);     cout<<mx<<endl;      } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}