#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n,r,b;    cin>>n>>r>>b;    ll qot=r/(b+1);    ll rem=r%(b+1);    string s="";    for (int i=0;i<n;i++){        s+='R';    }    if(rem==0){        for(int i=qot;i<n;i+=(qot+1)){            s[i]='B';        }    }    else{                int i=0;        for( i=qot+1;i<rem*(qot+2);i=(i+qot+2)){            s[i]='B';            if((i+qot+2)>=rem*(qot+2)){                break;            }                            }        //cout<<i<<endl;        for(int j=i;j<n;j=j+(qot+1)){            s[j]='B';        }    }    cout<<s<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}