#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    int n;    cin>>n;    int k;    cin>>k;    string s;    cin>>s;    int w=0;    vector<int> W(n);        for(int i=0;i<n;i++){        if(s[i]=='W'){            w++;        }        W[i]=w;     }    int mn=w;    mn=min(mn,W[k-1]);    for(int i=k;i<n;i++){        int cc=(W[i]-W[i-k]);        mn=min(mn,cc);    }    cout<<mn<<endl;        } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}