#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    int n;    cin>>n;    vector<int> arr(n);    for (int i=0;i<n;i++){        cin>>arr[i];    }    if(n==1){        cout<<-1<<endl;return;    }    if(arr[0]!=arr[1]){        cout<<-1<<endl;return;    }    if(arr[n-1]!=arr[n-2]){        cout<<-1<<endl;return;    }    vector<int> ans(n);    ans[0]=1;    for (int i=1;i<n;i++){        if(arr[i]!=arr[i+1] && arr[i]!=arr[i-1]){            cout<<-1<<endl;return;        }        else if(arr[i]==arr[i-1]){            ans[i]=ans[i-1];            ans[i-1]=i+1;        }        else if(i+1<n && arr[i]==arr[i+1]){            ans[i]=(i+2);            ans[i+1]=(i+1);            i++;        }             }    for (auto ele:ans){        cout<<ele<<" ";    }    cout<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}