#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;cin>>n;    ll x;cin>>x;    vector<ll> arr(n);    for(ll i=0;i<n;i++){        cin>>arr[i];    }    ll c=0;    ll mn=arr[0];    ll mx=arr[0];    for(ll i=1;i<n;i++){        mn=min(mn,arr[i]);        mx=max(mx,arr[i]);        if(mx-mn>2*x){            c++;            mx=arr[i];            mn=arr[i];        }    }    cout<<c<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}