#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n,k,b,s;    cin>>n>>k>>b>>s;    ll mn=k*b;    ll mx=mn+(k-1)*n;    if(s<mn || s>mx){        cout<<-1<<endl;        return;    }    vector<ll> arr(n);    arr[0]=mn;    s=s-mn;    for(ll i=0;i<n;i++){        arr[i]+=min(s,k-1);        s-=min(s,k-1);    }         for(auto ele:arr){        cout<<ele<<" ";    }    cout<<endl;  } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}