#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;    cin>>n;    ll d;    cin>>d;    vector <ll> arr(n);    for (ll i=0;i<n;i++){        cin>>arr[i];    }    sort(arr.begin(),arr.end(),greater<int>());    ll team=0;    ll c=0;    for (ll i=0;i<n;i++){        if(arr[i]>d){            team++;            c++;            continue;        }        ll f=0;        for (ll j=2;j<=(n-c);j++){            if(arr[i]*j>d){                c+=j;                team++;                f=1;                break;            }            }        if(f==0){            break;        }            }    cout<<team<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     solve();    return 0;}