#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;    cin>>n;    char c;    cin>>c;    string s;    cin>>s;    ll a=0;    ll b=0;    ll ans=0;    if(c=='g'){        cout<<0<<endl;        return;    }        for (int i=0;i<n;i++){        if(s[i]==c){            a=i;            while(i<n ){                                if(s[i]=='g'){                    b=i;                    break;                }                i++;            }            ans=max(ans,(b-a));         }    }    if(s[n-1]=='g'){        cout<<ans<<endl;        return;    }    ll f=-1;    ll l=-1;    for (int i=n-1;i>=0;i--){        if(s[i]==c){            l=i;                    }        if(s[i]=='g'){                        break;        }    }    if(l==-1){        cout<<ans<<endl;        return;    }    for (int i=0;i<n;i++){        if(s[i]=='g'){            f=i;            break;        }    }        ans=max(ans,(n+f-l));    cout<<ans<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}