#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;    cin>>n;    if (n%2==0){        cout<<n/2<<" "<<n/2<<endl;        return;    }        ll prime=-1;    for (ll i=2;i*i<=n;i++){        if(n%i==0){            prime=i;            break;        }    }    if(prime==-1){        cout<<1<<" "<<n-1<<endl;        return;    }    else{        ll ans=n/prime;        cout<<ans<<" "<<n-ans<<endl;        return;    } } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}