#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;    cin>>n;    string s;    cin>>s;    unordered_map<char,ll> mp1;    unordered_map<char,ll> mp2;    for (char c: s) {          mp1[c]++;    }    ll ans=0;    for (ll i=0;i<n;i++){        mp2[s[i]]++;        mp1[s[i]]--;        ll sz=mp2.size();        for (auto ele:mp1){            if(ele.second!=0){                sz++;            }        }        ans=max(ans,sz);     }    cout<<ans<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}