#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll n;    cin>>n;    vector<ll> arr(n);    ll sm=0;    ll neg=0;    ll mn=LLONG_MAX;    for(ll i=0;i<n;i++){        cin>>arr[i];        if(arr[i]<0){            neg++;        }        if(abs(arr[i])<mn){            mn=abs(arr[i]);        }        sm+=abs(arr[i]);    }    if(neg&1){        sm=sm-2*mn;    }    cout<<sm<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}