#include <bits/stdc++.h>using namespace std;#define ll long long int int main(){  ll t;  cin>>t;  while(t--){    ll n;    cin>>n;    vector<ll> a(n);    set<ll> st;    for (ll i=0;i<n;i++){      cin>>a[i];      st.insert(a[i]);    }    vector<ll> b(n);    for (ll i=0;i<n;i++){      cin>>b[i];      st.insert(b[i]);    }    unordered_map<ll,ll> mp1;    unordered_map<ll,ll> mp2;    mp1[a[0]]++;            ll c=1;    for (ll i=1;i<n;i++){         if(a[i-1]==a[i]){            c++;            mp1[a[i-1]]=max(c,mp1[a[i-1]]);            continue;         }         if(mp1[a[i]]==0){            mp1[a[i]]=1;         }                  c=1;            }    mp2[b[0]]++;    c=1;    for (ll i=1;i<n;i++){         if(b[i-1]==b[i]){            c++;            mp2[b[i-1]]=max(c,mp2[b[i-1]]);            continue;         }         if(mp2[b[i]]==0){            mp2[b[i]]=1;         }        c=1;            }    ll mx=1;    // for (auto ele:mp1){    //     cout<<ele.first<<" "<<ele.second<<" ";    // }    // cout<<endl;cout<<endl;    // for (auto ele:mp2){    //     cout<<ele.first<<" "<<ele.second<<" ";    // }    // cout<<endl;    // cout<<endl;    for (auto ele:st){        mx=max(mx,mp1[ele]+mp2[ele]);    }    cout<<mx<<endl;      }}