#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll n, k;    cin >> n >> k;    vector<ll> arr(n);    vector<pair<ll,ll>> ans;         for (ll i = 0; i < n; i++) {        cin >> arr[i];        if(arr[i] % k==0){            ans.push_back({ k, i + 1});        }        else{            ans.push_back({arr[i] % k, i + 1});        }            }     sort(ans.begin(), ans.end(), [](const pair<ll,ll> &a, const pair<ll,ll> &b) {        if (a.first != b.first)            return a.first > b.first;            return a.second < b.second;    });     for (auto ele : ans) {        cout << ele.second << " ";    }    cout << "\n";} int main() {    ios_base::sync_with_stdio(false);    cin.tie(nullptr);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}