#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll a,b,c,d;    cin>>a>>b>>c>>d;    if((max(a,b)+(min(a,b)+1)-1)/(min(a,b)+1)>2){      cout<<"NO"<<endl;      return;    }    if(a==c && b==d){      cout<<"YES"<<endl;      return;    }    int cn=c-a;    int dn=d-b;    if((max(cn,dn)+(min(cn,dn)+1)-1)/(min(cn,dn)+1)>2){      cout<<"NO"<<endl;      return;    }    else{      cout<<"YES"<<endl;      return;    }  } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}