#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    int n;    cin>>n;    int k;    cin>>k;    string s;    cin>>s;    s+='0';    int one=0;    int mx=0;    for (int i=0;i<=n;i++){      if(s[i]=='1'){        one++;      }      else{        mx=max(one,mx);        one=0;      }    }    if(mx>=k){      cout<<"NO"<<endl;      return;    }    vector <int > p(n);    int a=1;    int b=n;    for (int i=0;i<n;i++){      if(s[i]=='0'){        p[i]=b--;      }      else{        p[i]=a++;      }    }    cout<<"YES"<<endl;    for (auto ele:p){      cout<<ele<<" ";    }    cout<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}