#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; void solve() {    ll n;    cin>>n;    string s;    cin>>s;    ll c=0;    vl arr;    for(ll i=0;i<n;i++){        if(s[i]=='0'){            c++;            arr.push_back(i+1);        }    }    cout<<c<<endl;    for(auto ele:arr){        cout<<ele<<" ";    }    cout<<endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}