#include <bits/stdc++.h>using namespace std; using ll = long long;using vi = vector<ll>;using vl = vector<ll>;using pii = pair<int, int>;using pll = pair<ll, ll>;using vpii = vector<pii>;using vpll = vector<pll>; ll numberOfBits(long long n) {    ll bits = 0;    while (n > 0) {        bits++;        n >>= 1;      }    return bits;}  void solve() {    ll a;    ll b;    cin>>a;    cin>>b;    if(a==b){        cout<<0<<endl;        return;    }    ll bitsa= numberOfBits( a);    ll bitsb= numberOfBits( b);    if(bitsb>bitsa){        cout<<-1<<endl;        return;    }    ll pp=a^b;    if(pp<=a){        cout<<1<<endl;                cout<<pp<<endl;        return;    }    double pw=pow(2.0,double(bitsa-1));    ll pww=(ll)pw;     ll aaa=pww^a;     ll bbb=aaa^b;     cout<<2<<endl;    cout<<min(bbb,pww)<<" "<<max(bbb,pww)<<endl;          } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}