#include <bits/stdc++.h>using namespace std;using ll = long long;#define modd 998244353 void solve() {    ll n;    cin >> n;     vector<ll> a(n);    ll s = 0;    for (ll i = 0; i < n; i++) {        cin >> a[i];        s += abs(a[i]);    }    s -= abs(a[0]);    s += a[0];     if (n == 2) {        cout << max(a[0], -1*a[1]) << endl;        return;    }        ll tmp = 0;    ll mx = LLONG_MIN;     for (ll i = n - 1; i > 0; i--) {        mx = max(mx, s - abs(a[i]) - tmp);        tmp += a[i];        s -= abs(a[i]);    }     mx = max(mx, -1*tmp);    cout << mx << "\n";} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}