#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll n;    cin>>n;    vector<ll> arr(n);    for(ll i=0;i<n;i++){        cin>>arr[i];    }    ll sm=0;    for(ll i=1;i<n;i++){        sm+=abs(arr[i]-arr[i-1]);     }       ll mn=sm-abs(arr[1]-arr[0]);       mn=min(mn,sm-abs(arr[n-1]-arr[n-2]));       for(ll i=1;i<n-1;i++){        ll c=abs(arr[i]-arr[i-1])+abs(arr[i+1]-arr[i]);                ll d=abs(arr[i-1]-arr[i+1]);        mn=min(mn,sm-c+d);            }    cout<<mn<<endl;      } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}