#include <bits/stdc++.h>using namespace std; using ll = long long; void solve() {    ll n;    cin>>n;    vector<ll> arr(n);    for(ll i=0;i<n;i++){        cin>>arr[i];    }    sort(arr.begin(),arr.end());    ll diff=arr[1]-arr[0];    ll ans=diff>arr[0]?diff:arr[0];    cout<<ans<<endl;     } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}