#include <bits/stdc++.h>using namespace std;using ll = long long;#define modd 998244353using vll = vector<ll>; void solve() {    ll a, b;    cin >> a >> b;     ll a1 = a, b1 = b;    ll n1 = 1, n2 = 2;    ll c1 = 0, c2 = 0;     while (true) {        if (a1 >= n1) {            a1 -= n1;            c1++;        } else break;         if (b1 >= n2) {            b1 -= n2;            c2++;        } else break;         n1 <<= 2;        n2 <<= 2;    }     ll ans = 2 * min(c1, c2);    if (c1 != c2) ans++;     a1 = a;    b1 = b;    n1 = 2;    n2 = 1;    c1 = c2 = 0;     while (true) {        if (b1 >= n2) {            b1 -= n2;            c2++;        } else break;         if (a1 >= n1) {            a1 -= n1;            c1++;        } else break;         n1 <<= 2;        n2 <<= 2;    }     ll ans2 = 2 * min(c1, c2);    if (c1 != c2) ans2++;     cout << max(ans, ans2) << '\n';} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}