#include <bits/stdc++.h>using namespace std;using ll = long long;#define modd 998244353using vll = vector<ll>; void solve() {    ll n,k;    cin >> n >> k;    vll a(n);        for (ll i = 0; i < n; i++) {        cin >> a[i];            }    unordered_set<ll> st;    for(auto ele:a){        st.insert(ele);    }     for(int i=0;i<k-1;i++){        if(st.find(i)==st.end()){            cout<<i<<endl;            return;        }    }    cout<<k-1<<endl;          } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}