#include <bits/stdc++.h>using namespace std;using ll = long long;#define modd 998244353using vll = vector<ll>; void solve() {    ll n,ht,lt;    cin >> n>>ht>>lt;    ll h=max(ht,lt);    ll l=min(ht,lt);    vll a(n);    vll b(n,0);    for (ll i = 0; i < n; i++) {        cin >> a[i];     }    sort(a.begin(),a.end(),greater<ll>());        ll ans=0;    for (ll i = 0; i < n; i++) {        if(a[i]<=h && b[i]==0){            b[i]=1;            int f=0;            for(int j=i+1;j<n;j++){                if(a[j]<=l && b[j]==0){                                        b[j]=1;                    ans++;                    f=1;                    break;                }                             }                        if(f==0){                b[i]=0;            }        }                    }    cout<<ans<<endl;    } int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}