#include <bits/stdc++.h>using namespace std;using ll = long long;#define modd 998244353using vll = vector<ll>; void solve() {    ll n, x, y;    cin >> n >> x >> y;     vll a(n), contrib(n);    for (ll i = 0; i < n; i++) {        cin >> a[i];        contrib[i] = (a[i] / x) * y;    }     ll total = 0;    for (ll i = 0; i < n; i++) {        total += contrib[i];    }     ll ans = 0;    for (ll i = 0; i < n; i++) {        ans = max(ans, a[i] + (total - contrib[i]));    }     cout << ans << '\n';} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);     ll t;    cin >> t;    while (t--) {        solve();    }    return 0;}